mapper.insert:
INSERT INTO comment(
    parent_id
    , post_id
    , content
    , member_id
    , del_yn
    , create_date
    , update_date
) VALUES
(


            #{parentId}


          NULL


    , #{postId}
    , #{content}
    , #{memberId}
    , 'N'
    , NOW()
    , NOW()
)

mapper.update:
UPDATE comment
SET del_yn = 'Y'
WHERE comment_id IN ( /* 일반 댓글 삭제 시 하위 대댓글이 존재하는 경우 */
    SELECT comment_id
    FROM (
        SELECT comment_id
        FROM comment
        WHERE comment_id = #{commentId}
        OR parent_id = #{parentId}
    ) A
)

UPDATE comment
SET del_yn = 'Y'
WHERE comment_id = #{commentId}

mapper.select:
select COUNT(1) AS cnt
from comment
WHERE comment_id IN ( /* 일반 댓글 삭제 시 하위 대댓글이 존재하는 경우 */
    SELECT comment_id
    FROM (
         SELECT comment_id
         FROM comment
         WHERE parent_id = #{commentId}
     ) A
)

mapper.update:
UPDATE comment
SET content = #{content}
WHERE comment_id = #{commentId}
